import { useState, useEffect, useReducer } from 'react';
import Navbar from './components/Navbar';
import Card from "./components/Card";
import UploadForm from "./components/UploadForm";
import './App.css';

const photos=[];
const initialState={
    items: photos,
    count: photos.length,
    inputs: {title:null, file:null, path:null},
    isCollapsed: false
}
const handleOnChange=(state, e)=>{
  if(e.target.name === 'file'){
    return{...state.inputs, file: e.target.files[0], path:URL.createObjectURL(e.target.files[0])};
  }else{
    return{...state.inputs,title:e.target.value};
  }
}

function reducer(state, action){
  switch(action.type){
    case 'setItem':
      return{
        ...state,
        items:[state.inputs, ...state.items]
      }

    case 'setInputs':
      return{
        ...state,
        inputs:handleOnChange(state, state.payload.value)
      }
      default: return state;
  }
}

function App() {
  const [state, dispatch]= useReducer(reducer,initialState);
  const [count, setCount]=useState();
  const [isCollapsed, collapse]= useState(false);

  const toggle=()=> collapse(!isCollapsed);
  const handleOnChange=(e)=> dispatch({type: 'setInputs', payload: {value:e}})

  const handleOnSubmit=(e)=>{
    e.preventDefault();
    // setItems([inputs.path, ...items]);
    dispatch({type: 'setItem'});
    setInput({title:null, file:null, path:null});
    collapse(false);
  }

  useEffect(()=>{
    console.log(state);
  },[state.items])

